import React, { useState } from "react";

export default function TextDescription(props) {
  const clickUpHandler = () => {
    let newText = text.toUpperCase();
    setText(newText);
  };
  const clickLowerHandler = () => {
    let newText = text.toLowerCase();
    setText(newText);
  };
  const clickRemoveHandler = () => {
    let newText = "";
    setText(newText);
  };
  const changeHandler = (event) => {
    setText(event.target.value);
  };
   const clickCopyHandler = (event) => {
    let text = document.getElementById('myText');
    text.ariaSelected()

  };
  const [text, setText] = useState("Enter your Text here....");
  return (
    <>
      <div className="container">
        <h1>{props.heading}</h1>
        <hr />
        <div className="form-group">
          <textarea
            className="form-control"
            id="myText"
            value={text}
            onChange={changeHandler}
            rows="12"
          ></textarea>
        </div>
        <div>
          <button className="btn btn-primary mr-3" onClick={clickUpHandler}>
            Convert to Uppercase
          </button>

          <button className="btn btn-info mr-3" onClick={clickLowerHandler}>
            Convert to Lower Case
          </button>

          <button className="btn btn-danger" onClick={clickRemoveHandler}>
            Clear Text
          </button>
          <button className="btn btn-success" onClick={clickCopyHandler}>
            Clear Text
          </button>
        </div>
      </div>
      <div className="container my-4">
        <h1>Your Texts Summary</h1>
        <hr />
        <p>
          Total Words:
          <span className="border border-success p-2 ml-3 font-weight-bolder">
            {text.split(" ").length}
          </span>
        </p>
        <hr />
        <p>
          Total character:{" "}
          <span className="border border-info p-2 ml-3 font-weight-bolder">
            {text.length}
          </span>
        </p>
        <hr />
        <p>
          Can be read (approxmatly*):{" "}
          <span className="border border-info p-2 ml-3 mr-1 font-weight-bolder">
            {0.008 * text.split(" ").length}
          </span>
          minitue
        </p>
        <hr />
        <h2>Text Preview</h2>
        <hr />
        <p className="text-justify">{text}</p>
      </div>
    </>
  );
}
