import React, { useState } from "react";

export default function TextDescription(props) {
  const clickUpHandler = () => {
    let newText = text.toUpperCase();
    setText(newText);
  };
  const clickLowerHandler = () => {
    let newText = text.toLowerCase();
    setText(newText);
  };
  const clickRemoveHandler = () => {
    let newText = <text className="remove"></text>;
    setText(newText);
  };
  const changeHandler = (event) => {
    setText(event.target.value);
  };
  const [text, setText] = useState("Enter your Text here....");
  return (
    <>
      <div className="container">
        <h1>{props.heading}</h1>
        <hr />
        <div className="form-group">
          <textarea
            className="form-control"
            id="myText"
            value={text}
            onChange={changeHandler}
            rows="12"
          ></textarea>
        </div>
        <div>
          <button className="btn btn-primary mr-3" onClick={clickUpHandler}>
            Convert to Uppercase
          </button>

          <button className="btn btn-info mr-3" onClick={clickLowerHandler}>
            Convert to Lower Case
          </button>

          <button className="btn btn-info" onClick={clickRemoveHandler}>
            Remove Text
          </button>
        </div>
      </div>
      <div className="container my-4">
        <h1>Your Texts Summary</h1>
        <hr />
        <p>
          Total Words:
          <span class="border border-success p-2 ml-3">
            {text.split(" ").length}
          </span>
        </p>
        <hr />
        <p>
          Total character:{" "}
          <span class="border border-info p-2 ml-3">{text.length}</span>
        </p>
        <hr />
        <p>
          Can be read (approxmatly*):{" "}
          <span class="border border-info p-2 ml-3 mr-1">
            {0.008 * text.split(" ").length}
          </span>
          minitue
        </p>
        <hr />
      </div>
    </>
  );
}
