import React, { useState } from "react";

export default function TextDescription(props) {
  const clickUpHandler = () => {
    let newText = text.toUpperCase();
    setText(newText);
    props.showAlert("Text Converted to Upper Case.", "success");
  };
  const clickLowerHandler = () => {
    let newText = text.toLowerCase();
    setText(newText);
    props.showAlert("Text Converted to Lower Case.", "success");
  };
  const clickRemoveHandler = () => {
    let newText = "";
    setText(newText);
    props.showAlert("Text Field Clered Successfully.", "success");
  };
  let startTime;
  const changeHandler = (event) => {
    setText(event.target.value);
    let lenght = text.split(" ").filter((element) => {
      return element.length !== 0;
    }).length;
    if (!startTime) {
      startTime = Date.now();
    }
    const timeElapsed = (Date.now() - startTime) / 1000;
    const wpm = Math.round((wordCount / timeElapsed) * 60);
    console.log(timeElapsed);
  };
  const clickCopyHandler = () => {
    let text = document.getElementById("myText");
    text.select();
    navigator.clipboard.writeText(text.value);
    props.showAlert("Text Copied Successfully.", "success");
  };
  const clickExtraSpaces = (event) => {
    let newText = text.split(/[ ]+/);
    setText(newText.join(" "));
    props.showAlert(
      "Text Fields Extra Spaces Removed Successfully.",
      "success"
    );
  };

  const clickCamelCase = () => {
    let newText = text
      .trim()
      .replace(/[-_\s]+(.)?/g, (_, c) => (c ? c.toUpperCase() : ""));
    setText(newText);
    // console.log(newText);
  };
  const clickSnakCase = () => {
    let newText = text.replace(/\s+/g, "_");
    setText(newText);
    // console.log(newText);
  };
  const [text, setText] = useState("");
  return (
    <>
      <div
        className="container"
        style={{
          color: props.mode === "dark" ? "white" : "black",
        }}
      >
        <h1>{props.heading}</h1>
        <hr />
        <div className="form-group">
          <textarea
            style={{
              backgroundColor: props.mode === "dark" ? "#343a40" : "white",
              color: props.mode === "dark" ? "white" : "black",
            }}
            className="form-control"
            id="myText"
            value={text}
            onChange={changeHandler}
            rows="12"
          ></textarea>
        </div>
        <div className="mt-2">
          <button
            disabled={text.length === 0}
            className="btn btn-primary "
            onClick={clickUpHandler}
          >
            Convert to Uppercase
          </button>

          <button
            disabled={text.length === 0}
            className="btn btn-info mx-3"
            onClick={clickLowerHandler}
          >
            Convert to Lower Case
          </button>

          <button
            disabled={text.length === 0}
            className="btn btn-danger"
            onClick={clickRemoveHandler}
          >
            Clear Text
          </button>
          <button
            disabled={text.length === 0}
            className="btn btn-success mx-3"
            onClick={clickCopyHandler}
          >
            Copy Text
          </button>
          <button
            disabled={text.length === 0}
            className="btn btn-warning mx-3"
            onClick={clickExtraSpaces}
          >
            Remove Extra space
          </button>
          <button
            disabled={text.length === 0}
            className="btn btn-success"
            onClick={clickCamelCase}
          >
            Convert to CamelCase
          </button>
          <button
            disabled={text.length === 0}
            className="btn btn-success mx-3"
            onClick={clickSnakCase}
          >
            Convert to Snake_case
          </button>
        </div>
      </div>
      <div
        className="container my-4"
        style={{
          color: props.mode === "dark" ? "white" : "black",
        }}
      >
        <h1>Your Texts Summary</h1>
        <hr />
        <p>
          Total Words:
          <span className="border border-success p-2 ml-3 font-weight-bolder">
            {
              text.split(" ").filter((element) => {
                return element.length !== 0;
              }).length
            }
          </span>
        </p>
        <hr />
        <p>
          Typing Speed(Word per Minute):
          <span className="border border-success p-2 ml-3 font-weight-bolder">
            {
              text.split(" ").filter((element) => {
                return element.length !== 0;
              }).length
            }
          </span>
        </p>
        <hr />
        <p>
          Total character:{" "}
          <span className="border border-info p-2 ml-3 font-weight-bolder">
            {text.length}
          </span>
        </p>
        <hr />
        <p>
          Can be read (approxmatly*):{" "}
          <span className="border border-info p-2 ml-3 mr-1 font-weight-bolder">
            {0.008 *
              text.split(" ").filter((element) => {
                return element.length !== 0;
              }).length}
          </span>
          minitue
        </p>
        <hr />
        <h2>Text Preview</h2>
        <hr />
        <p className="text-justify">{text}</p>
      </div>
    </>
  );
}
